events.javaScript:
let add = false;
let x = typeof $ !== 'undefined' ? $('[name="country_id"]')[0] : document.querySelector('[name="country_id"]');
for (i = 0; i < x.options.length; i++) {
    if (x.options[i].text === "Vietnam") {
        if(add) {
            x.options[i].selected = true;
        } else {
            x.options.selectedIndex = i;
        }
    }
}

