let add = false;
let x = typeof $ !== 'undefined' ? $('#coupon_type')[0] : document.querySelector('#coupon_type');
for (i = 0; i < x.options.length; i++) {
    if (x.options[i].text === "For Total Orders") {
        if(add) {
            x.options[i].selected = true;
        } else {
            x.options.selectedIndex = i;
        }
    }
}

